const resultSpan=document.querySelector("#result-span"),stack=[];function pressNumberButton(t){"0"===getCurrentResult()?resultSpan.innerHTML=t:resultSpan.innerHTML+=t}function clickPeriod(){var t=getCurrentResult();t.includes(",")||setCurrentResult(t+",")}function processOperator(t){var e=getCurrentResult();"x"===t?stack.push(parseFloat(e),"*"):"&divide"===t?stack.push(parseFloat(e),"/"):stack.push(e,t),setCurrentResult(0)}function pressEqual(){var t=getCurrentResult(),t=(stack.push(t),calculate());setCurrentResult(t)}function clickPercentage(){const current=getCurrentResult(),result=eval(current+"/100");setCurrentResult(result),clearStack()}function toggleNegative(){const current=getCurrentResult(),toggleResult=eval(current+" * -1");setCurrentResult(toggleResult)}function getCurrentResult(){return resultSpan.innerHTML}function setCurrentResult(t){resultSpan.innerHTML=t}function clearResult(){setCurrentResult(0),clearStack()}function calculate(){const input=stack.join(""),result=eval(input);return clearStack(),result}function clearStack(){stack.splice(0,stack.length)}